mapper.select:
SELECT

FROM question a
WHERE a.id = #{id}

	AND a.subject = #{subject}

SELECT

FROM question a

	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.subject = #{subject}
	
		AND a.type = #{type}
	

ORDER BY a.id, a.update_date DESC

SELECT

FROM question a

	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.subject = #{subject}
	
		AND a.type = #{type}
	
	
		AND a.title LIKE 
			concat('%',#{title},'%')
			'%'||#{title}||'%'
	

ORDER BY a.id, a.update_date DESC
LIMIT ${(page.pageNo-1)*page.pageSize}, #{page.pageSize}

SELECT count(1) FROM question a

	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.subject = #{subject}
	
		AND a.type = #{type}
	
	
		AND a.title LIKE 
			concat('%',#{title},'%')
			'%'||#{title}||'%'

mapper.update:
UPDATE question SET
	del_flag = #{DEL_FLAG_DELETE},
	update_date = #{updateDate}
WHERE id = #{id}
